    /*exercicio 1*/
    private void Mutiplicacao(){
        int Result;
        int varnum1;
        int varnum2;
        varnum1 = Integer.parseInt(jTNUM1.getText());
        varnum2 = Integer.parseInt(jTNUM2.getText());
        Result = varnum1 * varnum2;        
        jTMULTIPLICACAO.setText(String.valueOf(Result));
    }
    
    private void Divisao(){
        int Result;
        int varnum1;
        int varnum2;
        varnum1 = Integer.parseInt(jTNUM1.getText());
        varnum2 = Integer.parseInt(jTNUM2.getText());
        Result = varnum1 / varnum2;        
        jTDIVISAO.setText(String.valueOf(Result));
    }
    
    private void Adicao(){
        int Result;
        int varnum1;
        int varnum2;
        varnum1 = Integer.parseInt(jTNUM1.getText());
        varnum2 = Integer.parseInt(jTNUM2.getText());
        Result = varnum1 + varnum2;        
        jTADICAO.setText(String.valueOf(Result));
    }
    
    private void Subtracao(){
        int Result;
        int varnum1;
        int varnum2;
        varnum1 = Integer.parseInt(jTNUM1.getText());
        varnum2 = Integer.parseInt(jTNUM2.getText());
        Result = varnum1 - varnum2;        
        jTSUBTRACAO.setText(String.valueOf(Result));
    }
