    /*exercicio 3*/    
    private void Orcamento(){
        double Result;
        double cadernos;
        double canetas;
        double regua;
        double calculadora;
        cadernos = 1.50 * 7;
        canetas = 0.25 * 2;
        regua = 0.15;
        calculadora = 5.5;                
        Result = (cadernos + canetas + regua + calculadora);       
        jTRESULTADO3.setText(String.valueOf(Result));        
    }
